import React from "react";
import DatePicker from 'react-datepicker';
import { useState, useContext } from "react";
import { AppContext } from "../Context";
const Form = () => {
    const { insertUser } = useContext(AppContext);
    const [newUser, setNewUser] = useState({});
    const [date, setDate] = useState(new Date());

    const MyContainer = ({ className, children }) => {
        return (
          <div style={{ padding: "16px", background: "#216ba5", color: "#fff" }}>
            <CalendarContainer className={className}>
              <div style={{ background: "#f0f0f0" }}>
                What is your favorite day?
              </div>
              <div style={{ position: "relative" }}>{children}</div>
            </CalendarContainer>
          </div>
        );
      };

    // Storing the Insert User Form Data.
    const addNewUser = (e, field) => {
        setNewUser({
            ...newUser,
            [field]: e.target.value,
        });
    };

    // Inserting a new user into the Database.
    const submitUser = (e) => {
        e.preventDefault();
        insertUser(newUser);
        e.target.reset();
    };

    return (
        <form className="insertForm" onSubmit={submitUser}>
            <h2>Insert Data</h2>
            <label htmlFor="_Amount">Amount</label>
            <input
                type="Number"
                id="_email"
                onChange={(e) => addNewUser(e, "user_email")}
                placeholder="Enter Amount"
                autoComplete="off"
                required
            />
            <label htmlFor="_name">Description </label>
            <input
                type="text"
                id="_name"
                onChange={(e) => addNewUser(e, "user_name")}
                placeholder="Enter Discrpition"
                autoComplete="off"
                required
            />
            <DatePicker
                selected={date}
                onChange={(date) => setDate(date)}
                onCalendarClose={handleCalendarClose}
                onCalendarOpen={handleCalendarOpen}
            />

            <input type="submit" value="Insert" />
        </form>
    );
};

export default Form;
